// Free job intent detection without AI APIs
const input = $input.first();
let userMessage = '';
let chatId = null;
let triggerType = 'unknown';
let keywords = 'python engineer';
let isJobRequest = false;

// Determine trigger type and extract data
if (input.json.message) {
  // Telegram trigger
  triggerType = 'telegram';
  userMessage = input.json.message.text || '';
  chatId = input.json.message.chat.id;
} else if (input.json.body) {
  // Webhook trigger
  triggerType = 'webhook';
  const body = typeof input.json.body === 'string' ? JSON.parse(input.json.body) : input.json.body;
  userMessage = body.message || body.query || '';
  keywords = body.keywords || keywords;
  chatId = body.chat_id || null;
  isJobRequest = true; // Webhook always triggers job search
} else {
  // Manual or scheduled trigger
  triggerType = input.json.node ? 'scheduled' : 'manual';
  isJobRequest = true; // Manual/scheduled always trigger job search
  userMessage = 'Job search requested';
}

// Simple keyword-based job detection for Telegram messages
if (triggerType === 'telegram') {
  const jobKeywords = [
    'job', 'jobs', 'work', 'position', 'career', 'employment', 
    'hiring', 'opening', 'opportunity', 'vacancy', 'praca'
  ];

  const techKeywords = [
    'python', 'javascript', 'java', 'developer', 'engineer', 
    'programmer', 'software', 'backend', 'frontend', 'fullstack'
  ];

  const message = userMessage.toLowerCase();

  isJobRequest = jobKeywords.some(keyword => message.includes(keyword)) ||
                 techKeywords.some(keyword => message.includes(keyword));

  // Extract keywords from user message if present
  if (isJobRequest) {
    const foundTech = techKeywords.find(tech => message.includes(tech));
    if (foundTech) {
      keywords = foundTech === 'javascript' ? 'javascript developer' : 
                foundTech === 'java' ? 'java developer' :
                foundTech + ' engineer';
    }
  }
}

// Generate response
let response = '';
if (isJobRequest) {
  response = triggerType === 'telegram' ? 
    'ðŸ” Searching for job opportunities...' :
    'Job search initiated';
} else {
  // Non-job related responses for Telegram
  const greetings = ['hello', 'hi', 'hey', 'good morning', 'good afternoon'];
  const isGreeting = greetings.some(greeting => userMessage.toLowerCase().includes(greeting));

  if (isGreeting) {
    response = 'ðŸ‘‹ Hello! I can help you find job opportunities. Just ask me about jobs, or mention technologies like "python jobs" or "javascript positions".';
  } else {
    response = 'ðŸ¤– I\'m a job search assistant. Ask me about job opportunities or specific technologies like Python, JavaScript, etc.';
  }
}

return [{
  json: {
    is_job_related: isJobRequest,
    answer: response,
    user_message: userMessage,
    chat_id: chatId,
    trigger_type: triggerType,
    keywords: keywords,
    search_initiated: isJobRequest
  }
}];

// Process jobs without external dependencies
const input = $input.first();
const triggerData = $('Free Intent Detector').first().json;

try {
  if (!input || !input.json) {
    throw new Error('No API response received');
  }

  const response = input.json;

  // Handle API errors
  if (response.success === false) {
    const errorMsg = response.error || 'Unknown API error';
    return [{
      json: {
        success: false,
        message: `âŒ Error: ${errorMsg}`,
        trigger_type: triggerData.trigger_type,
        chat_id: triggerData.chat_id
      }
    }];
  }

  // Handle empty results
  if (!response.jobs || response.jobs.length === 0) {
    return [{
      json: {
        success: true,
        message: `ðŸ” No jobs found for "${triggerData.keywords}". Try different keywords or check back later!`,
        jobs_found: 0,
        trigger_type: triggerData.trigger_type,
        chat_id: triggerData.chat_id
      }
    }];
  }

  // Process jobs into smaller batches
  const jobs = response.jobs;
  const batchSize = 3;
  const maxJobs = 15;
  const jobsToProcess = jobs.slice(0, maxJobs);

  // Create summary for non-Telegram triggers
  if (triggerData.trigger_type !== 'telegram') {
    const summary = {
      total_jobs: jobsToProcess.length,
      keywords: triggerData.keywords,
      timestamp: new Date().toISOString(),
      jobs: jobsToProcess.map(job => ({
        title: job.title,
        company: job.company,
        location: job.location,
        technologies: job.technologies,
        link: job.link,
        score: job.score
      }))
    };

    return [{
      json: {
        success: true,
        summary: summary,
        message: `Found ${jobsToProcess.length} jobs for ${triggerData.keywords}`,
        trigger_type: triggerData.trigger_type
      }
    }];
  }

  // Create batches for Telegram
  const batches = [];

  for (let i = 0; i < jobsToProcess.length; i += batchSize) {
    const batch = jobsToProcess.slice(i, i + batchSize);

    // Simple text formatting (no MarkdownV2 complexity)
    const message = batch.map((job, index) => {
      const title = job.title || 'No title';
      const company = job.company || 'Unknown company';
      const location = job.location || 'Location not specified';
      const technologies = job.technologies && job.technologies.length > 0 
        ? job.technologies.join(', ')
        : 'Technologies not listed';

      let jobText = `ðŸ“Œ ${title}\n`;
      jobText += `ðŸ¢ Company: ${company}\n`;
      jobText += `ðŸ“ Location: ${location}\n`;
      jobText += `ðŸ’¡ Tech: ${technologies}\n`;

      if (job.link && job.link !== 'N/A' && job.link !== '') {
        jobText += `ðŸ”— Link: ${job.link}\n`;
      }

      if (job.score && job.score > 0) {
        jobText += `â­ Match: ${Math.round(job.score * 100)}%\n`;
      }

      return jobText;
    }).join('\n' + '-'.repeat(30) + '\n\n');

    batches.push({
      json: {
        message: message,
        batch_number: Math.floor(i / batchSize) + 1,
        total_batches: Math.ceil(jobsToProcess.length / batchSize),
        jobs_in_batch: batch.length,
        chat_id: triggerData.chat_id,
        trigger_type: triggerData.trigger_type
      }
    });
  }

  return batches;

} catch (error) {
  console.error('Job processing error:', error);

  return [{
    json: {
      success: false,
      message: `âŒ Error processing jobs: ${error.message}`,
      error: error.message,
      trigger_type: triggerData.trigger_type,
      chat_id: triggerData.chat_id
    }
  }];
}

// Add delays between Telegram messages
const batchNumber = $json.batch_number || 1;
const delay = (batchNumber - 1) * 1500; // 1.5 seconds between batches

if (delay > 0) {
  await new Promise(resolve => setTimeout(resolve, delay));
}

return $input.all();

